// Parse the JSON from the output field, handling code blocks
if (aiData.output && typeof aiData.output === 'string') {
  try {
    let jsonString = aiData.output;
    if (jsonString.includes('```json')) {
      jsonString = jsonString.replace(/```json\s*/, '').replace(/\s*```$/, '');
    }
    aiData = JSON.parse(jsonString);
  } catch (e) {
    console.log('Error parsing JSON:', e);
    return [{ json: { company_name: 'Parse Error', error: e.message } }];
  }
}

// Enhanced parsing function for ARR values (handles M and B suffixes)
const parseARR = (value) => {
  if (!value) return null;

  let strValue = String(value).trim();
  console.log('Parsing ARR value:', strValue);

  // Remove currency symbols and spaces
  let clean = strValue.replace(/[$,\s]/g, '');

  // Handle billions - keep in billions for storage
  if (clean.toLowerCase().includes('billion') || clean.toLowerCase().includes('b')) {
    let numStr = clean.replace(/billion?/gi, '').replace(/b$/gi, '');
    let num = parseFloat(numStr.replace(/[^\d.]/g, ''));
    return num ? num : null; // Keep as billion value
  }

  // Handle millions - convert to decimal billions for consistency
  if (clean.toLowerCase().includes('million') || clean.toLowerCase().includes('m')) {
    let numStr = clean.replace(/million?/gi, '').replace(/m$/gi, '');
    let num = parseFloat(numStr.replace(/[^\d.]/g, ''));
    return num ? num / 1000 : null; // Convert millions to billions
  }

  // Handle raw numbers (assume they're already in billions if large, millions if small)
  let num = parseFloat(clean.replace(/[^\d.]/g, ''));
  if (num > 100) {
    return num / 1000; // Assume it's in millions, convert to billions
  }
  return num || null;
};

const parsePercent = (value) => {
  if (!value) return null;
  return parseFloat(String(value).replace(/[%\s]/g, '')) / 100;
};

// Parse current ARR (prioritize arr_estimate, fallback to revenue_estimate)
const currentARR = parseARR(aiData.arr_estimate || aiData.revenue_estimate);
console.log('Parsed current ARR:', currentARR);

// Format ARR for display - input should be in billions
const formatARR = (value) => {
  if (!value) return 'N/A';
  if (value >= 1) {
    return `${value.toFixed(1)}B`;
  }
  return `${(value * 1000).toFixed(0)}M`;
};

